<script>
	import {capitalize} from '@svizzle/utils';
	import Icon from '@svizzle/ui/src/icons/Icon.svelte';
	import Download from '@svizzle/ui/src/icons/feather/Download.svelte';
	import Menu from '@svizzle/ui/src/icons/feather/Menu.svelte';
	import MessageSquare from '@svizzle/ui/src/icons/feather/MessageSquare.svelte';
	import X from '@svizzle/ui/src/icons/feather/X.svelte';
	import A11yPerson from '@svizzle/ui/src/icons/svizzle/A11yPerson.svelte';
	import Link from '@svizzle/ui/src/Link.svelte';

	import theme from 'app/theme';
	import {version} from 'app/utils/version';

	const changelogUrl = 'https://github.com/nestauk/beis-indicators/blob/staging/CHANGELOG.md';

	// `_screen` shape at https://github.com/nestauk/svizzle/tree/dev/packages/components/ui/src/gauges/screen#screen-store
	export let _screen;
	export let contentHeight;
	export let segment;
	export let showA11yMenu = false;
	export let isA11yDirty = false;

	let showMenu = false;

	const closeMenu = () => showMenu = false;
	const toggleA11yMenu = () => showA11yMenu = !showA11yMenu;
	const toggleMenu = () => showMenu = !showMenu;

	$: makeColor = id => segment === id ? theme.colorMain : undefined;
</script>

<nav
	class={$_screen?.classes}
	style='--content-height: {contentHeight}px'
>
	{#if !$_screen?.sizes.medium}
		<header>
			{segment ? capitalize(segment) : 'Home'}
		</header>
		{#if !$_screen?.sizes.medium}
			<div
				class='clickable'
				on:click={toggleA11yMenu}
			>
				<Icon
					glyph={A11yPerson}
					stroke={isA11yDirty ? 'white' : 'black'}
					strokeWidth=1
					fill={isA11yDirty ? 'black' : ''}
				/>
			</div>
		{/if}
		<div
			class='clickable'
			on:click={toggleMenu}
		>
			{#if showMenu}
				<Icon glyph={X} />
			{:else}
				<Icon glyph={Menu} />
			{/if}
		</div>
	{/if}
	{#if !$_screen?.sizes.medium && showMenu || $_screen?.sizes.medium}
		<menu on:click={closeMenu}>
			<div>
				<ul>
					<li class:selected='{segment === undefined}'>
						<Link
							href='.'
							theme={{color: makeColor('.')}}
						>
							Home
						</Link>
					</li>
					<li class:selected='{segment === 'methodology'}'>
						<Link
							href='methodology'
							theme={{color: makeColor('methodology')}}
						>
							Methodology
						</Link>
					</li>
					<li class:selected='{segment === 'guide'}'>
						<Link
							href='guide'
							theme={{color: makeColor('guide')}}
						>
							Guide
						</Link>
					</li>
					<li class:selected='{segment === 'indicators'}'>
						<Link
							href='indicators'
							rel='prefetch'
							theme={{color: makeColor('indicators')}}
						>
							Indicators
						</Link>
					</li>
				</ul>
			</div>
			<div>
				<ul>
					<li>
						<Link href='download'>
							Download
							<Icon
								glyph={Download}
								size=20
								strokeWidth=1.5
							/>
						</Link>
					</li>
					<li>
						<Link
							href='feedback'
							theme={{color: makeColor('feedback')}}
						>
							Feedback
							<Icon
								glyph={MessageSquare}
								size=20
								strokeWidth=1.5
							/>
						</Link>
					</li>
					<li>
						<Link
							href={changelogUrl}
							isExternal={true}
						>
							{version}
						</Link>
					</li>
					{#if $_screen?.sizes.medium}
						<li
							on:click={toggleA11yMenu}
							class='clickable'
							class:selected='{segment === 'feedback'}'
						>
							<Icon
								glyph={A11yPerson}
								stroke={isA11yDirty ? 'white' : 'black'}
								strokeWidth=1
								fill={isA11yDirty ? 'black' : ''}
							/>
						</li>
					{/if}
				</ul>
			</div>
		</menu>
	{/if}
</nav>

<style>
	nav, menu {
		align-items: center;
		display: flex;
		height: 100%;
		width: 100%;
		z-index: var(--z-1000);
	}
	nav {
		gap: 12px;
		justify-content: end; /* Align to the right on Firefox */
		justify-content: flex-end; /* Align to the right on Chrome */
		position: relative;
	}
	header {
		color: var(--color-link);
		font-weight: bold;
		margin: 0 auto;
		position: absolute;
		text-align: center;
		width: 100%;
	}
	div {
		z-index: var(--z-1000);
	}
	menu {
		background: white;
		flex-direction: column-reverse;
		height: var(--content-height);
		justify-content: space-evenly;
		left: 0;
		position: fixed;
		top: 0;
		width: 100%;
	}
	.medium menu {
		flex-direction: row;
		height: 100%;
		justify-content: space-between;
		position: unset;
	}
	ul {
		align-items: center;
		display: flex;
		flex-direction: column-reverse;
		margin: 0;
		padding: 0;
	}
	.medium ul {
		flex-direction: row;
	}
	li {
		height: 100%;
	}
	li {
		display: block;
		float: left;
		margin-right: 0.5rem;
		padding: 1em 0.5em;
		white-space: nowrap;
	}
	li.selected {
		display: none;
	}

	.medium li.selected {
		color: var(--color-link);
		display: inline-block;
		position: relative;
	}
	li {
		display: block;
		float: none;
		margin-right: 0;
		padding: 1.5em 0.5em;
	}
	.medium li {
		padding: 0 0.5em;
	}
</style>
