<script>
	import {capitalize} from '@svizzle/utils';
	import ExternalLink from '@svizzle/ui/src/ExternalLink.svelte';
	import Icon from '@svizzle/ui/src/icons/Icon.svelte';
	import Download from '@svizzle/ui/src/icons/feather/Download.svelte';
	import Menu from '@svizzle/ui/src/icons/feather/Menu.svelte';
	import MessageSquare from '@svizzle/ui/src/icons/feather/MessageSquare.svelte';
	import X from '@svizzle/ui/src/icons/feather/X.svelte';

	import A11y from 'app/components/glyphs/A11y.svelte';
	import {version} from 'app/utils/version';

	const changelogUrl = 'https://github.com/nestauk/beis-indicators/blob/staging/CHANGELOG.md';

	// `_screen` shape at https://github.com/nestauk/svizzle/tree/dev/packages/components/ui/src/gauges/screen#screen-store
	export let _screen;
	export let contentHeight;
	export let segment;
	export let showA11yMenu = false;
	export let isA11yDirty = false;

	let showMenu = false;

	const closeMenu = () => showMenu = false;
	const toggleA11yMenu = () => showA11yMenu = !showA11yMenu;
	const toggleMenu = () => showMenu = !showMenu;
</script>

<nav
	class={$_screen?.classes}
	style='--content-height: {contentHeight}px'
>
	{#if !$_screen?.sizeFlags.medium}
		<header>
			{segment ? capitalize(segment) : 'Home'}
		</header>
		{#if !$_screen?.sizeFlags.medium}
			<div
				on:click={toggleA11yMenu}
				class='clickable'
			>
				<Icon
					glyph={A11y}
					stroke={isA11yDirty ? 'white' : 'black'}
					strokeWidth=1
					fill={isA11yDirty ? 'black' : ''}
				/>
			</div>
		{/if}
		<div on:click={toggleMenu}>
			{#if showMenu}
				<Icon glyph={X} />
			{:else}
				<Icon glyph={Menu} />
			{/if}
		</div>
	{/if}
	{#if !$_screen?.sizeFlags.medium && showMenu || $_screen?.sizeFlags.medium}
		<menu on:click={closeMenu}>
			<div>
				<ul>
					<li class:selected='{segment === undefined}'>
						<a href='.'>Home</a>
					</li>
					<li class:selected='{segment === 'methodology'}'>
						<a href='methodology'>Methodology</a>
					</li>
					<li class:selected='{segment === 'guide'}'>
						<a href='guide'>Guide</a>
					</li>
					<li class:selected='{segment === 'indicators'}'>
						<a
							rel='prefetch'
							href='indicators'
						>Indicators</a>
					</li>
				</ul>
			</div>
			<div>
				<ul>
					<li>
						<a href='/download'>
							Download
							<Icon
								glyph={Download}
								size=20
								strokeWidth=1.5
							/>
						</a>
					</li>
					<li class:selected='{segment === 'feedback'}'>
						<a href='feedback'>
							Feedback
							<Icon
								glyph={MessageSquare}
								size=20
								strokeWidth=1.5
							/>
						</a>
					</li>
					<li>
						<ExternalLink href={changelogUrl} text={version}/>
					</li>
					{#if $_screen?.sizeFlags.medium}
						<li
							on:click={toggleA11yMenu}
							class='clickable'
							class:selected='{segment === 'feedback'}'
						>
							<Icon
								glyph={A11y}
								stroke={isA11yDirty ? 'white' : 'black'}
								strokeWidth=1
								fill={isA11yDirty ? 'black' : ''}
							/>
						</li>
					{/if}
				</ul>
			</div>
		</menu>
	{/if}
</nav>

<style>
	nav, menu {
		align-items: center;
		display: flex;
		height: 100%;
		width: 100%;
		z-index: var(--z-1000);
	}
	nav {
		gap: 12px;
		justify-content: end; /* Align to the right on Firefox */
		justify-content: flex-end; /* Align to the right on Chrome */
		position: relative;
	}
	header {
		color: var(--color-link);
		font-weight: bold;
		margin: 0 auto;
		position: absolute;
		text-align: center;
		width: 100%;
	}
	div {
		z-index: var(--z-1000);
	}
	menu {
		background: white;
		flex-direction: column-reverse;
		height: var(--content-height);
		justify-content: space-evenly;
		left: 0;
		position: fixed;
		top: 0;
		width: 100%;
	}
	.medium menu {
		flex-direction: row;
		height: 100%;
		justify-content: space-between;
		position: unset;
	}
	ul {
		align-items: center;
		display: flex;
		flex-direction: column-reverse;
		margin: 0;
		padding: 0;
	}
	.medium ul {
		flex-direction: row;
	}
	li, a {
		height: 100%;
	}
	li {
		display: block;
		float: left;
		margin-right: 0.5rem;
		padding: 1em 0.5em;
		white-space: nowrap;
	}
	li.selected {
		display: none;
	}

	.medium li.selected {
		color: var(--color-link);
		display: inline-block;
		position: relative;
	}
	li {
		display: block;
		float: none;
		margin-right: 0;
		padding: 1.5em 0.5em;
	}
	.medium li {
		padding: 0 0.5em;
	}
	li .selected a {
		font-weight: bold;
	}

	a {
		display: block;
		font-size: 3vh;
		text-decoration: none;
		user-select: none;
	}
	.medium a {
		font-size: inherit;
	}
</style>
