<!-- TODO port to @svizzle/ui -->
<script>
	import {createEventDispatcher} from 'svelte';
	import {makeStyleVars} from '@svizzle/dom';
	import uid from 'uid';

	const dispatch = createEventDispatcher();
	const defaultTheme = {
		width: '50px',
		height: '24px',
		color: 'grey',
		backgroundColor: 'white',
		knobColor: 'lightgrey'
	}

	export let initial;
	export let values;
	export let theme;
	export let showLabels = true;

	const id = uid();

	$: value = initial;
	$: isRight = value === values[1];
	$: theme = {...defaultTheme, theme};
	$: style = makeStyleVars(theme);

	function toggle() {
		value = value === values[0] ? values[1] : values[0];
		dispatch('toggled', value);
	}
</script>

<div class="switch" {style} on:click={toggle}>
	<fieldset role="radiogroup">
		{#if showLabels}
			<label for="left-{id}" class:greyed={value !== values[0]}>{values[0]}</label>
		{/if}
		<span class="wrapper" class:isRight>
			<input type="radio" id="left-{id}" bind:group={value} value="{values[0]}">
			<input type="radio" id="right-{id}" bind:group={value} value="{values[1]}">
			<span aria-hidden="true" class="bkg"></span>
			<span aria-hidden="true" class="knob"></span>
		</span>
		{#if showLabels}
			<label for="right-{id}" class:greyed={value !== values[1]}>{values[1]}</label>
		{/if}
	</fieldset>
</div>

<style>
	fieldset {
		border: none;
		user-select: none;
		padding: 0;
	}

	.switch {
		cursor: pointer;
		display: flex;
		align-items: center;
	}
	.switch label {
		font-size: 16px;
		margin: 0 .5em 0;
		cursor: pointer;
	}
	.switch label.greyed {
		opacity: 0.5;
	}
	.switch input[type="radio"] {
		display: inline-block;
		margin-right: -2px;
		width: 50%;
		height: 100%;
		opacity: 0;
		position: relative;
		z-index: 1;
		cursor: pointer;
	}

	.wrapper {
		display: inline-block;
		vertical-align: middle;
		width: var(--width);
		height: var(--height);
		border-radius: calc(2 * var(--height));
		border: 1px solid var(--color);
		position: relative;
	}
	.knob {
		background: var(--knobColor);
		border-radius: 50%;
		border: 1px solid var(--color);
		display: block;
		height: 100%;
		left: 0;
		position: absolute;
		right: 100%;
		top: 0;
		transition: all 0.1s ease-out;
		width: 50%;
		z-index: 2;
	}

	.bkg {
		background-color: var(--backgroundColor);
		border-radius: 2em;
		cursor: pointer;
		display: block;
		height: 100%;
		left: 0;
		position: absolute;
		top: 0;
		transition: all 0.2s ease-out;
		width: 100%;
		z-index: 0;
	}

	.isRight .knob {
		right: 0;
		left: 50%;
	}

	.isRight .bkg {
		background-color: #fff;
	}
</style>
